# Create a dynamic column delimiter using regex for AMR
import re
def f_get_delimiter (source_path):
    try:
        headerlist = spark.sparkContext.textFile(source_path).take(1)
        header_str = ''.join(headerlist)

        results= re.search("(,|;|\\|)",header_str)
        return results.group()
    except Exception as err:
        print("Error Occured ", str(err))
amr_Filename = 'abfss://rawdata@rawdevstorage.dfs.core.windows.net/%s/P6523_Verbruiken_AMR_GEMEENTE_%s.csv' % (FolderName, FolderName_Without_Separator)
amr_delimiter_type = (f_get_delimiter(amr_Filename))

